mapper.select:
select

from enford_product_category
where 1=1

  and code = #{code,jdbcType=INTEGER}


  and parent = #{parent,jdbcType=INTEGER}

select distinct count(*) from enford_product_commodity a, enford_product_category b, enford_market_research_commodity c where a.category_code = b.code and a.id = c.cod_id
and b.parent = #{parent,jdbcType=INTEGER}
and c.res_id = #{resId,jdbcType=INTEGER}
order by b.code asc

select count(*) from enford_market_research_commodity a, enford_market_research_dept b,
enford_product_price c, enford_product_category d
where a.res_id = b.res_id = c.res_id
and a.res_id = #{resId,jdbcType=INTEGER}
and b.exe_id = #{deptId,jdbcType=INTEGER}
and c.com_id = a.cod_id
and b.comp_id = c.comp_id
and d.parent = #{parent,jdbcType=INTEGER};
